window.addEventListener("DOMContentLoaded",(()=>{"use strict";const e=document.querySelector("form"),t=document.querySelector(".form__textarea"),r=e.querySelector(".form__btn"),n=e.querySelectorAll("select"),a=document.querySelector(".result__textarea"),c=document.querySelector(".result__btn");t.value="первая строка ключевых строк\nвторая строка ключевых слов",a.value="",r.addEventListener("click",(e=>{e.preventDefault(),a.value="",t.value.split(/\r?\n/).forEach((e=>{n.forEach((t=>{switch(t.value){case"first":a.value+='"'+e.split(" ").map((e=>"!"+e)).join(" ")+'"\n';break;case"second":a.value+="["+e+"]\n";break;case"third":a.value+=function(e){return e.split(" ").map((e=>"+"+e)).join(" ")}(e)+"\n";break;case"fourth":a.value+=function(e){return"'"+e.split(" ").map((e=>"+"+e)).join(" ")}(e)+"\n";break;case"fifth":a.value+=function(e){return'"'+e+'"'}(e)+"\n";break;case"none":return}}))}))})),c.addEventListener("click",(e=>{e.preventDefault(),a.select(),document.execCommand("copy")}))}));